}

export const addBlog = (blog) => {
    console.log('TODO')
}

export const removeBlog = (id) => {

    return async dispatch => {
        const removeStatus = await blogService.remove(id)
        if (removeStatus === 204) {
            dispatch({
                type: 'REMOVE',
                data: id
            })
        } else {
            dispatch({
                type: 'FAILED',
                data: id
            })
        }
    }
}

export const initBlogs = () => {

    return async dispatch => {
        const blogs = await blogService.getAll()
        dispatch({
            type: 'INIT',
            data: blogs
        })
    }
}

export default reducer
