import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import Login from './components/Login'
import BlogForm from './components/BlogForm'
import Notification from './components/Notification'
import BlogList from './components/BlogList'
import Users from './components/Users'
import { initUser, logoutUser } from './reducers/userReducer'
import { initUsers } from './reducers/usersReducer'
import { initBlogs } from './reducers/blogReducer'
import { useDispatch } from 'react-redux'
import {
  useRouteMatch,
  Switch, Route, Link,
  useHistory
} from 'react-router-dom'

const App = (props) => {
  const [blogFormVisible, setBlogFormVisible] = useState(false)
  const history = useHistory()
  const dispatch = useDispatch()

  const match = useRouteMatch('/users/:id')

  useEffect(() => {
    dispatch(initBlogs())
  }, [])

  useEffect(() => {
    dispatch(initUser())
  }, [])

  useEffect(() => {
      dispatch(initUsers())
  }, [])

  const handleLogout = () => {
    props.logoutUser()
  }

  const userLoggedIn = () => {
    if (props.user === null) return false
    if (props.user.length === 0) return false
    if (props.user.token) return true
    else return false
  }

  const blogData = () => {
    const hideWhenVisible = { display: blogFormVisible ? 'none' : '' }
    const showWhenVisible = { display: blogFormVisible ? '' : 'none' }

    return (
      <div>
        <div style={hideWhenVisible}>
          <button className="toggle-blog-open" onClick={() => setBlogFormVisible(true)}>add blog</button>
        </div>

        <div style={showWhenVisible}>
          <BlogForm />
          <button onClick={() => setBlogFormVisible(false)}>cancel</button>
        </div>
        <h2>blogs</h2>
        <BlogList />
      </div>
    )
  }

  const siteHeader = () => {
    return (
      <div>
        <p>{props.user.name} logged in</p>
        <button onClick={() => handleLogout()}>logout</button>
      </div>
    )
  }

  const userView = () => {

    const userById = (id) =>
    props.users.find(a => a.id === id)
    const userMatch = match
      ? userById(match.params.id)
      : null
    console.log('matched', userMatch)

    return (
      <div>
        {userMatch.name}
        <div>
          {userMatch.blogs.map(blog => blog.title)}
        </div>
      </div>
    )
  }

  return (
    <div>
      {userLoggedIn() && siteHeader()}
      <Notification />
      <Switch>
        <Route path="/users/:id">
          {!userLoggedIn() && <Login />}
          {userLoggedIn() && userView()}
        </Route>
        <Route path="/users">
          {!userLoggedIn() && <Login />}
          {userLoggedIn() && <Users />}
        </Route>
        <Route path="/">
          {!userLoggedIn() && <Login />}
          {userLoggedIn() && blogData()}
        </Route>
      </Switch>
    </div>
  )
}

const mapDispatchToProps = {
  initBlogs,
  initUser,
  logoutUser
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
    users: state.users
  }
}

const ConnectedApp = connect(
  mapStateToProps,
  mapDispatchToProps
)(App)
export default ConnectedApp
