    switch (action.type) {
        case 'INIT': {
            return action.data
        }
        case 'ADD': {
            return state.concat(action.data)
        }
        case 'REMOVE': {
            const id = action.data
            return state.filter(blog => blog.id !== id)
        }
        default: return state
    }
}

