import React, { useState } from 'react'
import { connect } from 'react-redux'
import createComment from '../services/blogs'

const Blog = ({ blog, likeBlog, removeBlog }) => {
  const [comment, setComment] = useState('')

  const blogStyle = { margin: '10px', padding: '2px' }

  const handleComment = () => {
    setComment(comment)
  }


  return (
    <div style={blogStyle} className='blog'>

      <div>
        <h2>{blog.title} {blog.author}</h2>
        <div>{blog.url}</div>
        <div>likes: {blog.likes} <button className="blog-like-button" onClick={() => likeBlog(blog)}>like</button></div>
        <div>{blog.user.name}</div>
        <button style={ { background: 'red' } } className="blog-delete-button" onClick={() => removeBlog(blog)}>remove</button>
      </div>
      <div>
        <form onSubmit={() => createComment(comment, blog.id)}>
          <label>
            Add a comment:
            <input value={comment} onChange={handleComment} type="text" />
          </label>
        </form>
        <h3>comments</h3>
        <ul>
          {blog.comments.map(comment => <li key={comment.id}>{comment.content}</li>)}
        </ul>
      </div>
    </div>
  )
}

const mapDispatchToProps = {
  createComment
}

const ConnectedBlog = connect(
  null,
  mapDispatchToProps
)(Blog)
export default ConnectedBlog
