import React, { useState } from 'react'
import blogService from '../services/blogs'

const Blog = ({ blog, user, blogs }) => {
  const [blogInfoVisible, setBlogInfoVisible] = useState(false)

  const likeBlog = async (event) => {
    event.preventDefault()
  
    try {
      const newBlog = {
        user: blog.user.id,
        title: blog.title,
        author: blog.author,
        url: blog.url,
        likes: blog.likes + 1
      }
  
      const savedBlog = await blogService.put(newBlog, blog.id)
      if (savedBlog) {
        const newBlogList = blogs.concat(savedBlog)
        setBlogs(newBlogList)
        setBlogTitle('')
        setBlogAuthor('')
        setBlogURL('')
        console.log('blogs', newBlogList)
      }
  
    } catch (exception) {
      console.error('login exception', exception)
    }
  }

  const hideWhenVisible = { display: blogInfoVisible ? 'none' : '' }
  const showWhenVisible = { display: blogInfoVisible ? '' : 'none'}

  const blogStyle = { border: 'thin solid', margin: '2px', padding: '2px'}

  return (
  <div style={blogStyle}>
    <div style={hideWhenVisible}>
        {blog.title} {blog.author}
        <button onClick={() => setBlogInfoVisible(true)}>view</button>
      </div>

      <div style={showWhenVisible}>
        {blog.title} {blog.author} <button onClick={() => setBlogInfoVisible(false)}>hide</button>
        <div>{blog.url}</div>
        <div>likes: {blog.likes} <button>like</button></div>
        <div>{user}</div>
      </div>
  </div>
  )
}

export default Blog
