import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import Login from './components/Login'
import BlogForm from './components/BlogForm'
import blogService from './services/blogs'
import loginService from './services/login'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [blogTitle, setBlogTitle] = useState('')
  const [blogAuthor, setBlogAuthor] = useState('')
  const [blogURL, setBlogURL] = useState('')
  const [blogFormVisible, setBlogFormVisible] = useState(false)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password
      })

      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )
      blogService.setToken(user.token)
      setUser(user)
      console.log('user', user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      console.error('login exception', exception)
    }
  }

  const createBlog = async (event) => {
    event.preventDefault()

    try {
      const newBlog = {
        title: blogTitle,
        author: blogAuthor,
        url: blogURL
      }

      const savedBlog = await blogService.create(newBlog)
      if (savedBlog) {
        const newBlogList = blogs.concat(savedBlog)
        setBlogs(newBlogList)
        setBlogTitle('')
        setBlogAuthor('')
        setBlogURL('')
        console.log('blogs', newBlogList)
      }

    } catch (exception) {
      console.error('login exception', exception)
    }
  }

  const handleLogout = () => {
    setUser(null)
    window.localStorage.removeItem('loggedBlogappUser')
  }

  const handleUsername = (username) => {
    setUsername(username)
  }

  const handlePassword = (password) => {
    setPassword(password)
  }

  const blogData = () => (
    <div>
    <p>{user.name} logged in</p><button onClick={() => handleLogout()}>logout</button>
    <BlogForm blogTitle={blogTitle} blogAuthor={blogAuthor} blogURL={blogURL}
        setBlogTitle={(value) => setBlogTitle(value)}
        setBlogAuthor={(value) => setBlogAuthor(value)}
        setBlogURL={(value) => setBlogURL(value)}
        createBlog={createBlog}/>
    <h2>blogs</h2>
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )

  const blogForm = () => {
      
    const hideWhenVisible = { display: blogFormVisible ? 'none' : '' }
    const showWhenVisible = { display: blogFormVisible ? '' : 'none'}
  }

  return (
    <div>
      {user === null && <Login username={username} password={password} handleLogin={handleLogin} handleUsername={handleUsername} handlePassword={handlePassword}/>}
      {user !== null && blogData()}
    </div>
  )
}

export default App
